import React,{useState,useEffect} from 'react';
import {Redirect,Route,useHistory} from 'react-router-dom';
import axios from 'axios';
import swal from 'sweetalert';
import MasterAdmin from '../layouts/admin/MasterAdmin';

function PrivateAdminRoute({...rest}){

    const [loading,setLoading] = useState(true);
    const [Authenticated,setAuthenticated] = useState(false);

    useEffect(() => {

        axios.get('/api/checking-authentication').then(res => {

            if(res.status === 200)
            {
                setAuthenticated(true);
            }
            setLoading(false);

        });
        return () => {
            setAuthenticated(false);   
        }
    },[]);

    axios.interceptors.response.use(undefined,function axiosRetryInterceptor(err){

        if(err.response.status === 401)
        {
            swal("Unauthorized",'Unauthorized',"warning")
        }

    });

    if(loading)
    {
        return <h1 style={{marginTop:'230px',marginLeft:'600px',color:'grey'}}>Loading...</h1>
    }

    return(

        <Route {...rest}
        
            render={({props,location}) => 
                Authenticated ? 
                <MasterAdmin {...props} /> : 
                <Redirect to={ {pathname:'/Login',state : {from:location}} } />

            }

        />

    );

}

export default PrivateAdminRoute;
