import React from 'react';
import jsPDF from 'jspdf';


function Pdf(){

    const generatePdf = () => {

        var doc = new jsPDF('p','pt','a4');

        doc.html(document.querySelector("#content"),{
            callback: function(pdf){
                pdf.save("Facture.pdf");
            }
        });

    }

    return (
        <div className="container">
            <div id="content" style={{position:'absolute',width:'100%',height:'100%'}} align="center">
                <br/>
                <img src="login.png" style={{width:'100px',height:'100px'}} alt=""/>
                <h5 className="display-6">LR10 Town-Club</h5>
                <h6>NÂ° 1520</h6>
                <br/>
                <div className="row">
                    <div className="col-6">
                        <label >Nom Complete : </label>
                    </div>
                    <div className="col-6">
                        
                    </div>
                </div>
            </div>
            <br/>
            <button  className="btn btn-primary" onClick={(e) => generatePdf}>Generate Pdf</button>
        </div>
    );

}

export default Pdf;
