import React from 'react';
import {Link} from 'react-router-dom';


function Register(){

    const state = {
        name : '',
        email : '',
        password : '',
    }

    const handleInput = (e) => {

        this.setState({
            [e.target.name] : e.target.value
        });

    }

    return(
        <div align="center">
            <form  className="col-lg-4" style={{marginTop:'70px'}}>
                <Link to='TownClub/Home'><img className="mb-4" src="login.png" alt="" width="82" height="75"/></Link>
                <h1 className="h3 mb-3 fw-normal">Please sign up</h1>
                
                <div className="form-floating">
                    <input type="text" name="name" onChange={this.handleInput} value={this.state.name} className="form-control"  placeholder="Full Name"/>
                    <label for="floatingInput">Email address</label>
                </div>
                    <br/>
                <div className="form-floating">
                    <input type="email" name="email" onChange={this.handleInput} value={this.state.email} className="form-control"  placeholder="name@example.com"/>
                    <label for="floatingInput">Email address</label>
                </div>
                    <br/>
                <div className="form-floating">
                    <input type="password" name="password" onChange={this.handleInput} value={this.state.password} className="form-control"  placeholder="Password"/>
                    <label for="floatingPassword">Password</label>
                </div>
                <br/>
                <div className="checkbox mb-3">
                    <label>
                        <input type="checkbox" value="remember-me"/> Remember me
                    </label>
                </div>
                <br/>
                    <button className="w-100 btn btn-lg btn-primary" type="submit">Sign in</button>
                    <p className="mt-5 mb-3 text-muted">&copy; 2021</p>
            </form>
        </div>
    );

}

export default Register;
