import React,{useState} from 'react';
import axios from 'axios';
import {history} from 'react-router-dom';
import swal from 'sweetalert';

const AddMember = () => {

        let history = useHistory();

        const [memberInput,setMember] = useState({});

        const [error,setError] = useState([]);

        const handleInput = (e) => {

            e.persist();

            setMember({...memberInput,[e.target.name] : e.target.value});
        }

        const memberSubmit = (e) => {

            e.preventDefault();

            const data = memberInput;

            axios.post('/api/store-member',data).then( res => {

                if(res.data.status === 200)
                {
                    swal("Successfully",res.data.message,"success");
                    history.push('/admin/Member');
                }
                else if(res.data.status === 500)
                {
                    swal("Required",res.data.message,"error");
                    setError(res.data.errors);
                }

            });
        }

        return(
            <div className="container p-5 bg-light">
                        <h3>New Member</h3>
                        <hr style={{color:'#e67e22',height:'4px',width:'180px'}} />
                    <br/>
                    <br/>
                        <form >
                            <div className="row g-2">
                                <div className="col-lg-6">
                                    FullName : <input type="text"  name="name" onChange={handleInput} value={memberInput.name} className="form-control" />
                                    <br/>
                                    <span style={{color:'red'}}>{error.name}</span>
                                </div>
                                <div className="col-lg-6">
                                    C I N : <input type="text"  name="cin"  onChange={handleInput} value={memberInput.cin} className="form-control" />
                                    <br/>
                                    <span style={{color:'red'}}>{error.cin}</span>
                                </div>

                                <div className="col-lg-6">
                                    Télé : <input type="text"  name="tele"  onChange={handleInput} value={memberInput.tele} className="form-control" />
                                    <br/>
                                    <span style={{color:'red'}}>{error.tele}</span>
                                </div>
                                <div className="col-lg-6">
                                    Age : <input type="text"  name="age"  onChange={handleInput} value={memberInput.age} className="form-control" />
                                    <br/>
                                    <span style={{color:'red'}}>{error.age}</span>
                                </div>

                                <div className="col-lg-6">
                                    adresse : <input type="text"  name="adresse"  onChange={handleInput} value={memberInput.adresse} className="form-control" />
                                    <br/>
                                    <span style={{color:'red'}}>{error.adresse}</span>
                                </div>
                                <div className="col-lg-6">
                                    Abonnement : <input type="text"  name="subscribe"  onChange={handleInput} value={memberInput.subscribe} className="form-control" />
                                    <br/>
                                    <span style={{color:'red'}}>{error.subscribe}</span>
                                </div>
                                <div className="col-lg-6">
                                    Assurance Abonnement : <input type="text"  name="assurance"  onChange={handleInput} value={memberInput.assurance} className="form-control" />
                                    <br/>
                                    <span style={{color:'red'}}>{error.assurance}</span>
                                </div>
                                <div className="col-lg-6">
                                    Date Payment Assurance : <input type="text"  name="date_payment_assurance"  onChange={handleInput} value={memberInput.date_payment_assurance} className="form-control" />
                                    <br/>
                                    <span style={{color:'red'}}>{error.date_payment_assurance}</span>
                                </div>
                                <div className="col-lg-12">
                                    Etat : 
                                    <select type="text"  name="etat" onChange={handleInput} value={memberInput.etat} className="form-control">
                                        <option>On</option>
                                        <option>Off</option>
                                    </select>
                                    <br/>
                                    <span style={{color:'red'}}>{error.etat}</span>
                                </div>

                                <div className="col-lg-12" align="center">
                                    <button type="submit" onClick={memberSubmit} className="btn btn-outline-secondary" > Add </button>
                                    <br/>
                                </div>
                            </div>
                        </form>
                </div>
        );

}


export default AddMember;
