import React from 'react';
import jsPDF from 'jspdf';


function Pdf(){

    const generatePdf = (e) => {

        e.preventDefault();
        var doc = new jsPDF('p','pt','a3');

        doc.html(document.querySelector("#content"),{
            callback: function(pdf){
                pdf.save("Facture.pdf");
            }
        });

    }

    return (
        <div >
            <div id="content"  align="center">
                <br/>
                <img src="login.png" style={{width:'100px',height:'100px'}} alt=""/>
                <h6 className="display-6">LR10 Town-Club</h6>
                <h6>NÂ° 1520</h6>
                <br/>
                <div className="row">
                    <div className="col-4">
                        <label >Full Name :  Kamal Sonbol</label>
                        <br/>
                        <label >C.I.N : AQ 1257896</label>
                        
                    </div>
                    <div className="col-4">
                        <label >Subscribe Price : 200 DH's</label>
                        <br/>
                        <label >Member ID : 200</label>
                    </div>
                </div>
                <div className="row">
                    <div className="col-12">
                        <label align="center">Date Payment : 01/01/2020</label>
                    </div>
                </div>
            </div>
            <br/>
            <button  className="btn btn-primary" onClick={(e) => generatePdf(e)}>Generate Pdf</button>
        </div>
    );

}

export default Pdf;
