import React,{useState,useEffect} from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';
import swal from 'sweetalert';

const Member = () => {

    const [memberInput,setMember] = useState({});

    const [loading,setLoading] = useState(true);

    const deleteSubmit = (e,id) => {
        
        e.preventDefault();

        const thisClicked = e.currentTarget;
        
        thisClicked.innerText = 'Deleting...';

        axios.delete('/api/delete-member/'+id).then( res => {

            
            if(res.data.status === 200)
            {
                swal("Sucessfully",res.data.message,"success");
                thisClicked.closest('tr').remove();
            }else if(res.data.staus === 500)
            {
                swal("Warning",res.data.message,"warning");
                thisClicked.innerText = 'Delete';
            }

        });
    }

    useEffect( () => {

        axios.get('/api/show-member').then( res => {

            if(res.data.status === 200)
            {
                setMember(res.data.members);
            }
            setLoading(false);
        });
        
    },[]);

    var memberslist_HTML = "";

    if(loading)
    {
        return <div style={{marginTop:'220px'}} align="center">
                    <img src="/login.png" width="60px" alt='' width="50px" height="50px" /><h1 style={{color:'grey'}}>Loading...</h1>
               </div>
    }
    else
    {
         memberslist_HTML =
        memberInput.map(item => {

            return (
                <tr key={item.id}>
                    <th>{item.id}</th>
                    <th>{item.name}</th>
                    <th>{item.cin}</th>
                    <th>{item.tele}</th>
                    <th>{item.age}</th>
                    <th>{item.adresse}</th>
                    <th>{item.subscribe}</th>
                    <th>{item.assurance}</th>
                    <th>{item.date_assurance}</th>
                    <th>{item.date_payment_assurance}</th>
                    <th>{item.etat}</th>
                    <th>{item.created_at}</th>
                    <th>{item.updated_at}</th>
                    <th><Link to={'/admin/UpdateMember/'+item.id} className="btn btn-primary">Edit</Link>
                    &nbsp; <button onClick={ (e) => deleteSubmit(e,item.id)} className="btn btn-danger">Delete</button></th>
                </tr>
            )

        });
    }

        return(
            <div className="container p-5 ">
                <div >
                    <h3>Members List</h3>
                    <hr style={{color:'#e67e22',height:'4px',width:'170px'}} />
                </div>
                <br/>
                <form className="d-flex" align="center">
                    <input className="form-control m-2" type="search" list="datalistOptions" id="exampleDataList" placeholder="Type to search..."/>
                        <datalist id="datalistOptions">
                            <option value="San Francisco"/>
                            <option value="New York"/>
                            <option value="Seattle"/>
                            <option value="Los Angeles"/>
                            <option value="Chicago"/>
                        </datalist>
                    <button className="btn btn-outline-success btn-sm" id="btnsearch" type="submit"><i className="fas fa-search"></i></button>
                    &nbsp;<Link to="/admin/AddMember" className="btn btn-outline-success" id="btnsearch" >New Member</Link>
                </form>
                <br/>
                <br/>
                <div className="responsive-table">
                    <table className="table table-hover">
                        <thead>
                            <tr>
                                <th >ID</th>
                                <th >Full Name</th>
                                <th >CIN</th>
                                <th >Télé</th>
                                <th >Age</th>
                                <th >Adresse</th>
                                <th >Prix Abonnement</th>
                                <th >Assurance Abonnement</th>
                                <th >Date Assurance</th>
                                <th >Date Payment Assurance</th>
                                <th >Etat</th>
                                <th >Created_at</th>
                                <th >Updated_at</th>
                                <th >Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {memberslist_HTML}
                        </tbody>
                    </table>
                </div>
            </div>
        );

}


export default Member;
