import React from 'react';
import jsPDF from 'jspdf';


function Pdf(){

    const generatePdf = (e) => {

        e.preventDefault();
        var doc = new jsPDF('p','pt','a4');

        doc.html(document.querySelector("#content"),{
            callback: function(pdf){
                pdf.save("Facture.pdf");
            }
        });

    }

    return (
        <div >
            <div id="content"  align="center">
                <div className="row">
                    <div className="col-10">
                        <div className="row">
                            <div className="col-12">
                                <div className="row">
                                    <div className="col-6">
                                        <br/>
                                        <img src="login.png" style={{width:'100px',height:'100px'}} alt=""/>
                
                                        <h6 >LR10 Town-Club</h6>
                
                                        <h6>N° 1520</h6>
                
                                        <br/>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="col-6">
                                        <h6 >Full Name :  Kamal Sonbol</h6>
                                        <br/>
                                        <h6 >C.I.N : AQ 1257896</h6>
                                        <br/>
                                        <h6 >Subscribe Price : 200 DH's</h6>
                                        <br/>
                                        <h6 >Member ID : 200</h6>
                                        <br/>
                                        <h6 >Date Payment : 01/01/2020</h6>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <br/>
            <button  className="btn btn-primary" onClick={(e) => generatePdf(e)}>Generate Pdf</button>
        </div>
    );

}

export default Pdf;
