import React,{useState,useEffect} from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';


const Message = () => {

    const [messagesList,setMessages] = useState({});
    const [loading,setLoading] = useState(true);

    useEffect(()=> {

        axios.get('/api/show-message').then(res => {

            if(res.status === 200)
            {
                setMessages(res.data.messages);
            }
            setLoading(false);
        });
    
    },[]);

    var messageslist_HTML = '';

    if(loading)
    {
        return <h1 style={{marginTop:'230px',marginLeft:'500px',color:'grey'}}>Loading...</h1>
    }
    else
    {
         messageslist_HTML = 
         messagesList.map(item => {

            return(

                <tr key={item.id}>
                    <th>{item.id}</th>
                    <th>{item.name}</th>
                    <th>{item.email}</th>
                    <th>{item.title}</th>
                    <th>{item.message}</th>
                    <th>{item.created_at}</th>
                    <th>{item.updated_at}</th>
                    <th><Link className="btn btn-danger">Delete</Link></th>
                </tr>

            );

         });


    }

        

        return(
            <div className="container p-5 bg-light">
                <div >
                    <h3>Messages List</h3>
                    <hr style={{color:'#e67e22',height:'4px',width:'165px'}} />
                </div>
                <br/>
                <form className="d-flex" align="center">
                    <input className="form-control me-2" type="search" list="datalistOptions" id="exampleDataList" placeholder="Type to search..."/>
                        <datalist id="datalistOptions">
                            <option value="San Francisco"/>
                            <option value="New York"/>
                            <option value="Seattle"/>
                            <option value="Los Angeles"/>
                            <option value="Chicago"/>
                        </datalist>
                    <button className="btn btn-outline-success" id="btnsearch" type="submit"><i className="fas fa-search"></i></button>
                </form>
                <br/>
                <br/>
                
                    <table className="table table-hover">
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">Name</th>
                                <th scope="col">Email</th>
                                <th scope="col">Sujet</th>
                                <th scope="col">Message</th>
                                <th scope="col">Updated_at</th>
                                <th scope="col">Created_at</th>
                                <th scope="col">Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {messageslist_HTML}
                        </tbody>
                    </table>
                
            </div>
        );

}


export default Message;
