import React from 'react';
import {Link} from 'react-router-dom';


const Member = () => {

    const [memberInput,setMember] = useState([]);

    const [loading,setLoading] = useState([]);

    useEffect( (e) => {

        axios.get('/api/show-member').then( res => {

            if(res.data.status === 200)
            {
                setMember(res.data.members);
            }
            else if(res.data.status === 500)
            {
                swal("Warning",res.data.message,"warning");
            }

        });

    },[]);


        return(
            <div className="container p-5 bg-light">
                <div >
                    <h3>Members List</h3>
                    <hr style={{color:'#e67e22',height:'4px',width:'170px'}} />
                </div>
                <br/>
                <form className="d-flex" align="center">
                    <input className="form-control me-2" type="search" list="datalistOptions" id="exampleDataList" placeholder="Type to search..."/>
                        <datalist id="datalistOptions">
                            <option value="San Francisco"/>
                            <option value="New York"/>
                            <option value="Seattle"/>
                            <option value="Los Angeles"/>
                            <option value="Chicago"/>
                        </datalist>
                    <button className="btn btn-outline-success" id="btnsearch" type="submit"><i className="fas fa-search"></i></button>
                    &nbsp;<Link to="/admin/AddMember" className="btn btn-outline-success" id="btnsearch" >New Member</Link>
                </form>
                <br/>
                <br/>
                <div className="responsive-table">
                    <table className="table table-hover">
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">Full Name</th>
                                <th scope="col">CIN</th>
                                <th scope="col">Télé</th>
                                <th scope="col">Age</th>
                                <th scope="col">Adresse</th>
                                <th scope="col">Prix Abonnement</th>
                                <th scope="col">Assurance Abonnement</th>
                                <th scope="col">Date Payment Assurance</th>
                                <th scope="col">Etat</th>

                                <th scope="col">Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {memberslist_HTML}
                        </tbody>
                    </table>
                </div>
            </div>
        );

}


export default Member;
