import React,{useState,useEffect} from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';

const Users = () => {

        const [userInput,setUser] = useState([]);
        const [loading,setLoading] = useState(true);

        useEffect(() => {
            
            axios.get('/api/show-user').then( res => {

                if(res.data.status === 200)
                {
                    setUser(res.data.users);
                }
                setLoading(false);
            });
        },[]);

        var userlist_HTML = '';

        if(loading)
        {
            return <h1 style={{marginTop:'230px',marginLeft:'500px',color:'grey'}}>Loading...</h1>
        }
        else
        {
            userlist_HTML = 
            userInput.map(item => {

                return(
                    <tr key={item.id}>
                        <th>{item.id}</th>
                        <th>{item.name}</th>
                        <th>{item.email}</th>
                        <th>{item.age}</th>
                        <th>{item.tele}</th>
                        <th>{item.adresse}</th>
                        <th>{item.role_as}</th>
                        <th>{item.created_at}</th>
                    </tr>
                )

            });
        }

        return(
            <div className="container p-5 bg-light">
                <div >
                    <h3>Users List</h3>
                    <hr style={{color:'#e67e22',height:'4px',width:'155px'}} />
                </div>
                <br/>
                <form className="d-flex" align="center">
                    <input className="form-control me-2" type="search" list="datalistOptions" id="exampleDataList" placeholder="Type to search..."/>
                        <datalist id="datalistOptions">
                            <option value="San Francisco"/>
                            <option value="New York"/>
                            <option value="Seattle"/>
                            <option value="Los Angeles"/>
                            <option value="Chicago"/>
                        </datalist>
                    <button className="btn btn-outline-success" id="btnsearch" type="submit"><i className="fas fa-search"></i></button>
                    &nbsp;<Link to="/Register" className="btn btn-outline-success" id="btnsearch" >New User</Link>
                </form>
                <br/>
                <br/>
                <div className="responsive-table">
                    <table className="table table-hover">
                        <thead>
                            <tr>
                                <th >ID</th>
                                <th >Full Name</th>
                                <th >Email</th>
                                <th >Age</th>
                                <th >Adresse</th>
                                <th >Télé</th>
                                <th >Role AS</th>
                                <th >Created At</th>
                                <th >Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {userlist_HTML}
                        </tbody>
                    </table>
                </div>
            </div>
        );

}


export default Users;
